#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\deyan.marzev\source\repos\DeyanTestProject\HouseViewer\node_modules\@nuxt\telemetry\bin\node_modules;C:\Users\deyan.marzev\source\repos\DeyanTestProject\HouseViewer\node_modules\@nuxt\telemetry\node_modules;C:\Users\deyan.marzev\source\repos\DeyanTestProject\HouseViewer\node_modules\@nuxt\node_modules;C:\Users\deyan.marzev\source\repos\DeyanTestProject\HouseViewer\node_modules;C:\Users\deyan.marzev\source\repos\DeyanTestProject\node_modules;C:\Users\deyan.marzev\source\repos\node_modules;C:\Users\deyan.marzev\source\node_modules;C:\Users\deyan.marzev\node_modules;C:\Users\node_modules;C:\node_modules;C:\Users\deyan.marzev\source\repos\DeyanTestProject\HouseViewer\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/deyan.marzev/source/repos/DeyanTestProject/HouseViewer/node_modules/@nuxt/telemetry/bin/node_modules:/mnt/c/Users/deyan.marzev/source/repos/DeyanTestProject/HouseViewer/node_modules/@nuxt/telemetry/node_modules:/mnt/c/Users/deyan.marzev/source/repos/DeyanTestProject/HouseViewer/node_modules/@nuxt/node_modules:/mnt/c/Users/deyan.marzev/source/repos/DeyanTestProject/HouseViewer/node_modules:/mnt/c/Users/deyan.marzev/source/repos/DeyanTestProject/node_modules:/mnt/c/Users/deyan.marzev/source/repos/node_modules:/mnt/c/Users/deyan.marzev/source/node_modules:/mnt/c/Users/deyan.marzev/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules:/mnt/c/Users/deyan.marzev/source/repos/DeyanTestProject/HouseViewer/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../@nuxt/telemetry/bin/nuxt-telemetry.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../@nuxt/telemetry/bin/nuxt-telemetry.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../@nuxt/telemetry/bin/nuxt-telemetry.mjs" $args
  } else {
    & "node$exe"  "$basedir/../@nuxt/telemetry/bin/nuxt-telemetry.mjs" $args
  }
  $ret=$LASTEXITCODE
}
